-- tsql sql:
SELECT TOP 1 TRY_CAST(value AS INT) AS try_cast_value, TRY_CONVERT(INT, value) AS try_convert_value, TRY_PARSE(value AS INT) AS try_parse_value, CAST(value AS INT) AS cast_value, CONVERT(INT, value) AS convert_value, PARSENAME(value, 1) AS parsename_value, REPLACE(value, 'a', 'd') AS replace_value, REVERSE(value) AS reverse_value, FORMAT(TRY_CAST(value AS INT), 'D', 'en-US') AS format_value, IIF(value = 'a', 1, 0) AS iif_value, CHOOSE(1, value, 'b', 'c') AS choose_value, COALESCE(value, 'd') AS coalesce_value, NULLIF(value, 'a') AS nullif_value FROM (VALUES (1, 'a'), (2, 'b'), (3, 'c')) AS temp_result(id, value);
